INSERT INTO `pages` VALUES (2,'Latest News','guest','A page for latest news items','',NULL),(3,'Projects','guest','These are the main projects that I\'ve been involved with during my working life so far....more (and better) to come','Ben Roberts, Xoundboy, projects, sound engineering, sound design, mastering, recording, events','<p align=\"left\">In 1993 I began working as a freelance Music Producer, Sound Engineer, Studio Designer and installer of sound equipment. Since then I have been involved with many different projects some more successful than others.</p>\r\n<p>Here are some of them...</p>'),(4,'Travels','member',NULL,NULL,NULL),(5,'Wedding','administrator','','','<br />'),(6,'Contact','guest',NULL,NULL,NULL),(7,'Music','guest',NULL,NULL,NULL),(10,'Recordings','guest','Access to all Xoundboy recordings',NULL,NULL),(1,'Un-assigned','administrator','For items that have not yet been assigned to a page',NULL,NULL),(11,'About','guest','About Xoundboy Recordings and Ben Roberts\' background in sound, music and music production.','Ben Roberts, music, producion, sound engineer, Manchester, Prague, web developer, Xoundboy, Recordings',NULL),(12,'Evening Guests','guest','','','<p>Dear Wedding Guest,</p>\r\n<p>Welcome to Petra &amp; Ben\'s Wed Site.</p>\r\n<p>Here you will find information about our wedding that will be taking place at Vrtbovska Zahrada at 11:00h this Saturday, 3rd May. </p>\r\n<p>You are warmly invited to join us for the ceremony and for the Evening Recption at Petrinske Terasy which begins with the cake cutting at 18:30h and will end around 01:00h. </p>\r\n<p>looking forward to seeing you on our Big Day</p>\r\n<p>love from&nbsp; Petra &amp; Ben x</p>');
